<?phpinclude 'global.php';include('mail/htmlMimeMail.php');function send($id) {	$uid = (int)substr($id, 0, 4);	$cid = substr($id, -3);		$email = mssql_result(mssql_query("SELECT email FROM cadastro WHERE idCadastro=$uid"),0,0);	$curso = mssql_result(mssql_query("SELECT titulo FROM cursos WHERE idCurso=$cid"),0,0);		// parte text/html	$ds = date("w");	$mes = date("n");	$dia = substr(date("0d"), -2);		$ano = date("Y");		$nomeDia = array('Domingo','Segunda','Terça','Quarta','Quinta','Sexta','Sábado');	$nomeMes = array('','Janeiro','Fevereiro','Março','Abril','Maio','Junho','Julho','Agosto','Setembro','Outubro','Novembro','Dezembro');	$dataext =	$nomeDia[$ds] . ", $dia de " . $nomeMes[$mes] . " de $ano";			// parte text/html			$shtml = "		<html>			<head>				<title>AMF - Associação Mineira de Farmacêuticos</title>				<style>					p {font: normal 9pt Tahoma, Verdana, Arial, Helvetica, sans-serif;color: #808080}										td {padding:0 4px;font:normal 9pt Arial, Helvetica, sans-serif, Tahoma, Verdana;color:#808080}					a {font:bolder 9pt Arial, Helvetica, sans-serif, Tahoma, Verdana;color:#000066;text-decoration:none}										a:hover {color:#ff6600;text-decoration:underline;}										.boxborder {border:1px solid #f0f0f0}														.tfooter {background-color:#D8D4E6;text-align:center;font-size:12pt}				</style>					</head>					<body topmargin=4 leftmargin=4>					<table border=0 cellpadding=2 cellspacing=2 width=565 class=boxborder>					<tr>						<td rowspan=2 width='230'>							<a href='http://www.amfar.com.br'><img src='logoMail.gif' border='0' width='230'></a></td>						<td align=center style='width:330px'>Efetivação de Matrícula</td>					</tr>					<tr><td class=tcenter>$dataext</td></tr>										<tr>						<td colspan=2>							<p>Prezado Aluno,</p>							<p style='text-align:justify'>								Aproveitamos para reafirmar o nosso compromisso e empenho em manter a qualidade do serviço oferecido pela 								Associação Mineira de Farmacêuticos em parceria com o Centro Universitário Newton Paiva, que tem o reconhecimento 								e respeito da comunidade acadêmica a nível nacional.							</p>							<p>Para garantir sua vaga junto a AMF, você deverá:</p>							<ol>								<li><b>Imprimir e pagar o boleto em anexo<b/> referente à primeira parcela (nos casos de cursos com pagamento parcelado);</li>								<li><b>Imprimir e assinar o contrato de prestação de serviços<b/> (para cursos de especializações);</li>								<li><b>Enviar para a AMF os documetnos abaixo para efetivar a matrícula:</b>									<ol>										<li>Contrato assinado</li>										<li>Currículo Vitae mais histórico escolar da graduação</li>										<li>Uma foto 3 x 4</li>										<li>Cópia do Certificado de Graduação Autenticada</li>										<li>Cópia do CPF e Cópia do RG</li>									</ol>								</li> 							</ol>							<p align='center'>Estamos esperando por você!</p>							<p align='center'>Segue em anexo o boleto referente à primeira parcela e o contrato.</p>							<p align='center'><b>Obs.:</b> Caso já tenha enviado todos os documentos solicitados acima por favor, 								desconsidere essas solicitações!</p>							<p align='center'>Qualquer dúvida, entre em contato com amfar@amfar.com.br ou ligue 31 3291-6242</p>													</td>					</tr>					<tr>						<td class='tfooter' colspan='2' align='cente'>							<a href='http://www.amfar.com.br'>:: AMF Associação Mineira de Farmacêuticos ::</a></td>					</tr>				</table>			</body>		</html>";		$text = strip_tags($shtml);	$mailto = $email;	//$mailto = 'webmaster@sbrafh.org.br';	//$mailto = 'amfar@amfar.com.br';			$anexo = "boletos2011/$id.pdf";	$anexo2 = "boletos2011/{$id}contrato.pdf";	$mail = new htmlMimeMail();		$mail->setFrom("AMF <webmaster@amfar.com.br>");	$mail->setReplayTo('amfar@amfar.com.br');	$mail->setCc('amfar@amfar.com.br');	$mail->setSubject($curso.' / Efetivação de Matrícula');		$mail->setHtml($shtml, $text, 'images/');			$attachment = $mail->getFile($anexo);	$mail->addAttachment($attachment, basename($anexo), 'application/pdf');		$attachment = $mail->getFile($anexo2);	$mail->addAttachment($attachment,  basename($anexo2), 'application/pdf');		$result = $mail->send(array($mailto));	$msgMail = $result ? 'E-mail enviado com sucesso' : $mail->errors;		return($msgMail);	}$alunos[]="1820145";$alunos[]="1599145";$alunos[]="1762145";$alunos[]="1768145";$alunos[]="1594145";$alunos[]="1831145";$alunos[]="1817145";$alunos[]="1813145";$alunos[]="1576145";$alunos[]="1811145";$alunos[]="1805145";$alunos[]="1021145";$alunos[]="1761145";$alunos[]="1769145";$alunos[]="1767145";$alunos[]="1777145";$alunos[]="1781145";$alunos[]="1575145";$alunos[]="1788145";$alunos[]="1799145";$alunos[]="1802145";$alunos[]="1784144";$alunos[]="1815144";$alunos[]="0296144";$alunos[]="1786144";$alunos[]="1783144";$alunos[]="1801144";$alunos[]="1812144";$alunos[]="1543144";$alunos[]="1806144";$alunos[]="1795146";$alunos[]="1829146";$alunos[]="1803146";$alunos[]="1796146";$alunos[]="1794146";$alunos[]="1780146";$alunos[]="1743146";$alunos[]="1782146";$alunos[]="1657146";$alunos[]="1640146";foreach($alunos as $aluno):	//echo "aluno = $aluno";	send($aluno);endforeach;?>