<script>	function CallPrint(strId)	{		var prtContent = document.getElementById(strId);		var WinPrint = window.open('','','letf=0,top=0,width=1,height=1,toolbar=0,scrollbars=0,status=0');		WinPrint.document.write(prtContent.innerHTML);		WinPrint.document.close();		WinPrint.focus();		WinPrint.print();		WinPrint.close();	//	prtContent.innerHTML=strOldOne;	}</script><body topMargin=70 leftMargin=70><?$_meses[]='';$_meses[1]='Janeiro';$_meses[2]='Fevereiro';$_meses[3]='Março';$_meses[4]='Abril';$_meses[5]='Maio';$_meses[5]='Junho';$_meses[7]='Julho';$_meses[8]='Agosto';$_meses[9]='Setembro';$_meses[10]='Outubro';$_meses[11]='Novembro';$_meses[12]='Dezembro';include 'global.php';include 'includes/funcoesUteis.php';$cid = (int)$_GET['cid'];$uid = (int)$_GET['uid'];if (!$uid) die("ID do aluno não foi passado");if (!$cid) die("ID do curso não foi passado");$query=mssql_query("SELECT cr.cargaHoraria, cr.titulo,     	CONVERT(CHAR(10),cr.inicio,103) AS inicio,	CONVERT(CHAR(10),cr.final,103) AS final,	cr.valor, cr.parcelas,	cd.nome, cd.ender, cd.num, cd.compl, cd.bairro, cd.cidade, cd.uf, cd.rg, cd.cpf	FROM cursos AS cr, cadastro AS cd		WHERE cr.idCurso=$cid AND cd.idCadastro=$uid");$rsCurso=mssql_fetch_object($query);$rsCurso->cpf = formatCPF($rsCurso->cpf);$total = $rsCurso->valor;$parcelas = $rsCurso->parcelas;$parcela = number_format($rsCurso->valor / $rsCurso->parcelas, 2, '.','');//debug($rsCurso);$ames = explode('/', $rsCurso->inicio); $inicio = sprintf('%s/%s', str_replace('Ç','ç', strtolower($_meses[(int)$ames[1]])), $ames[2]);echo "<div id='toPrint'>";include 'contrato.html';echo "</div>";echo"<br/><br/>	<p style='border-top:1px dashed #000; text-align:center; padding:8px 0'>	<img style='cursor:pointer' src='images/printer.png' border='0' align='absMiddle' onclick=CallPrint('toPrint') />					&nbsp;<a href='JavaScript:void(null)' onclick=CallPrint('toPrint')>Imprimir</a></p>";?>